import React, { Component } from "react";
import AOS from "aos";
import "aos/dist/aos.css";
import Container from "../Styles/Container.css";
import JobCard from "../Styles/JobCard.css";
import wsib from "../Images/wsib.png";
import grandRiver from "../Images/grandRiverFoods.png";

export default class Experience extends React.Component {
  constructor(props) {
    super(props);
    this.state = { height: props.height, width: props.width };
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.height !== this.state.height) {
      this.setState({ height: nextProps.height });
    }
    if (nextProps.width !== this.state.width) {
      this.setState({ width: nextProps.width });
    }
  }

  render() {
    {
      AOS.init();
    }
    return (
      <div>
        <center>
          <div
            className="text"
            data-aos="fade-down"
            data-aos-easing="linear"
            data-aos-duration="500"
            data-aos-anchor-placement="top-center"
            data-aos-offset="-100"
          >
            My Job Experiences
          </div>
        </center>

        <center>
          <div
            style={{
              marginTop: 100,
              overflowY: "scroll"
            }}
          >
            <div
              className="main"
              style={{
                height: this.state.height / 3,
                width: this.state.width / 2.2,
                marginRight: this.state.width / 2,
                marginLeft: -this.state.width / 22,
                marginBottom: 100
              }}
              data-aos="slide-right"
              data-aos-easing="ease-in-cubic"
              data-aos-duration="500"
              data-aos-anchor-placement="top-center"
              data-aos-offset="-150"
            >
              <div
                className="picture"
                style={{
                  width: this.state.width / 6,
                  height: this.state.height / 3
                }}
              >
                <img
                  src={wsib}
                  height={this.state.height / 3.5}
                  width={this.state.width / 12}
                  style={{
                    borderStyle: "solid",
                    borderRadius: "10%",
                    marginTop: "8%"
                  }}
                />
              </div>
              <div className="secondRow">
                <div
                  className="title"
                  style={{
                    width: this.state.width / 3,
                    height: this.state.height / 10
                    //verticalAlign: "middle"
                  }}
                >
                  <center>
                    <div className="verticalAlign">
                      <p>WSIB Innovation Lab - Designer/Developer</p>
                      <p>September 2018 - December 2018</p>
                    </div>
                  </center>
                </div>

                <div
                  style={{
                    color: "white",
                    marginTop: 10,
                    textAlign: "left",
                    paddingLeft: 10,
                    fontFamily: "Avenir Next",
                    fontSize: 12
                  }}
                >
                  <p>
                    Responsible for the continued frontend and backend
                    development of WSIB’s web portal prototype
                  </p>
                  <p style={{ color: "black", fontSize: 6 }}>""</p>
                  <p>
                    Used ReactJS to code the frontend components while using a
                    mySQL backend to read and write data to the frontend
                  </p>
                  <p style={{ color: "black", fontSize: 6 }}>""</p>
                  <p>
                    Created a prototype mobile app as part of an independent
                    research project for the lab
                  </p>
                  <p style={{ color: "black", fontSize: 6 }}>""</p>
                  <p>
                    The app included several different pages (including a login
                    page) which were connected to a FireBase database
                  </p>
                </div>
              </div>
            </div>
            <div
              className="main"
              style={{
                height: this.state.height / 3,
                width: this.state.width / 2.2,
                marginLeft: this.state.width / 2,
                marginRight: -this.state.width / 22,
                marginBottom: 100
              }}
              data-aos="slide-left"
              data-aos-easing="ease-in-cubic"
              data-aos-duration="500"
              data-aos-anchor-placement="top-center"
              data-aos-offset="-150"
            >
              <div
                className="picture"
                style={{
                  width: this.state.width / 6,
                  height: this.state.height / 3
                }}
              >
                <img
                  src={wsib}
                  height={this.state.height / 3.5}
                  width={this.state.width / 12}
                  style={{
                    borderStyle: "solid",
                    borderRadius: "10%",
                    marginTop: "20%"
                  }}
                />
              </div>
              <div
                className="secondRow"
                style={{ width: this.state.width / 3 }}
              >
                <div
                  className="title"
                  style={{
                    width: this.state.width / 3,
                    height: this.state.height / 10
                    //verticalAlign: "middle"
                  }}
                >
                  <center>
                    <div className="verticalAlign">
                      <p>WSIB Innovation Lab - Designer/Developer</p>
                      <p>January 2018 - April 2018</p>
                    </div>
                  </center>
                </div>

                <div
                  style={{
                    color: "white",
                    marginTop: 10,
                    textAlign: "left",
                    paddingLeft: 10,
                    fontFamily: "Avenir Next",
                    fontSize: 12,
                    paddingRight: 15
                    //width: this.state.width / 3
                  }}
                >
                  <p>
                    Responsible for the frontend development of WSIB’s web
                    portal prototype
                  </p>
                  <p style={{ color: "black", fontSize: 6 }}>""</p>
                  <p>
                    Used tools such as Sketch to create and design the different
                    portal pages before they were coded
                  </p>
                  <p style={{ color: "black", fontSize: 6 }}>""</p>
                  <p>
                    Used ReactJS to code and build the different webpages while
                    using Javascript + CSS to create the different interactive
                    components within them
                  </p>
                  <p style={{ color: "black", fontSize: 6 }}>""</p>
                  <p>
                    Conducted user interviews in order to receive feedback on
                    the prototype that the lab was working on
                  </p>
                </div>
              </div>
            </div>
            <div
              className="main"
              style={{
                height: this.state.height / 3,
                width: this.state.width / 2.2,
                marginRight: this.state.width / 2,
                marginLeft: -this.state.width / 22,
                marginBottom: 100
              }}
              data-aos="slide-right"
              data-aos-easing="ease-in-cubic"
              data-aos-duration="500"
              data-aos-anchor-placement="top-center"
              data-aos-offset="-150"
            >
              <div
                className="picture"
                style={{
                  width: this.state.width / 6,
                  height: this.state.height / 3
                }}
              >
                <img
                  src={grandRiver}
                  height={this.state.height / 6}
                  width={this.state.width / 10}
                  style={{
                    borderStyle: "solid",
                    borderRadius: "10%",
                    marginTop: "30%"
                  }}
                />
              </div>
              <div className="secondRow">
                <div
                  className="title"
                  style={{
                    width: this.state.width / 3,
                    height: this.state.height / 10
                    //verticalAlign: "middle"
                  }}
                >
                  <center>
                    <div className="verticalAlign">
                      <p>WSIB Innovation Lab - Designer/Developer</p>
                      <p>September 2018 - December 2018</p>
                    </div>
                  </center>
                </div>

                <div
                  style={{
                    color: "white",
                    marginTop: 10,
                    textAlign: "left",
                    paddingLeft: 10,
                    fontFamily: "Avenir Next",
                    fontSize: 12
                  }}
                >
                  <p>
                    Responsible for the continued frontend and backend
                    development of WSIB’s web portal prototype
                  </p>
                  <p style={{ color: "black", fontSize: 6 }}>""</p>
                  <p>
                    Used ReactJS to code the frontend components while using a
                    mySQL backend to read and write data to the frontend
                  </p>
                  <p style={{ color: "black", fontSize: 6 }}>""</p>
                  <p>
                    Created a prototype mobile app as part of an independent
                    research project for the lab
                  </p>
                  <p style={{ color: "black", fontSize: 6 }}>""</p>
                  <p>
                    The app included several different pages (including a login
                    page) which were connected to a FireBase database
                  </p>
                </div>
              </div>
            </div>
          </div>
        </center>
      </div>
    );
  }
}
